import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';

const UpdateComp_data = () => {

    const { id } = useParams();
    const [compData, setCompData] = useState(null);
    const [loading, setLoading] = useState(false);

    const navigate = useNavigate();

    const getCompDataById = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/competition/getbyid/' + id)

        console.log(res.status)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setCompData(data)
            setLoading(false);
        }
    }

    useEffect(() => {
        getCompDataById();
    }, [])

    const updateCompData = async (formdata) => {
        console.log(formdata)
        const res = await fetch('http://localhost:5000/competition/update/' + id, {
            method: 'PUT',
            body: JSON.stringify(formdata),
            headers: {
                'Content-Type': 'application/json'
            }
        })

        console.log(res.status)

        if (res.status === 200) {
            Swal.fire({
                icon: 'success',
                title: 'Success',
                text: 'Data Updated Successfully'
            })
            navigate('/display')
        }
    }

    const showUpdateCompForm = () => {
        if (!loading && compData) {
            return (
                <div className='card mt-5'>
                    <div className="card-body">
                        <h1 className="text-center mb-3">Update your Data here</h1>

                        <Formik
                        initialValues={compData}
                        onSubmit={updateCompData}
                        >

                        {
                            ({values, handleChange, handleSubmit}) => (
                                <form onSubmit={handleSubmit}>
                                    < div className="">
                                            <label className="form-label text-dark" for="form12"><b>Company Name</b></label>
                                            <input name='name' type="text" id="form12" className="form-control mb-3" onChange={handleChange} value={values.name} />
                                        </div>
                                </form>
                            )
                        }

                        </Formik>
                        



                    </div>
                </div>
            )
        }
    }









    return (
        <div></div>
    )
}

export default UpdateComp_data
