import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';

const DisplayDetails = () => {

    const [currentUser, setCurrentUser] = useState(JSON.parse(sessionStorage.getItem('user')))
    console.log(currentUser)

    const [orgData, setOrgData] = useState([]);
    const [comp, setComp] = useState([]);
    const [loading, setLoading] = useState(false);

    // Orgnisation Data

    const fetchOrgData = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/organisation/getbyuser/' + currentUser._id)

        setLoading(false);
        console.log(res.status)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setOrgData(data)
        }
    }

    // Competition Data

    const fetchCompData = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/competition/getbyuser/' + currentUser._id)

        setLoading(false);
        console.log(res.status)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setComp(data)
        }
    }



    useEffect(() => {
        fetchOrgData();
    }, [])

    const displayData = () => {
        if (!loading && orgData && comp) {
            return (

                <div>
                    {
                        orgData.map((item) => (
                            <div className="card mt-3">
                                <div className="card-body">
                                    <h1 className="card-title">Organisation Name : {item.org_name}</h1>
                                    <p className="card-text">
                                        <b>About Organisation </b>: {item.org_details}
                                    </p>
                                    <p>
                                        <b>Email : </b> {item.email}
                                    </p>
                                    <h4>About Competition - </h4>
                     <p><b>Date and Time : </b>{competition.date}</p>
                     <p><b>Description : </b>{competition.description}</p>
                     <p><b>Rules : </b>{competition.rules}</p>
                        <p><b>Rewards : </b>{competition.rewards}</p>
                     <p><b>Venue : </b>{competition.venue}</p>
                                </div>
                            </div>
                        )



                        )
                    }
                </div>
            )



        }
    }







    return (
        <div className='container'>

            {displayData()}
        </div>
    )
}

export default DisplayDetails
