import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';

const DisplayDetails = () => {

    const [currentUser, setCurrentUser] = useState(JSON.parse(sessionStorage.getItem('user')))
    console.log(currentUser)

    const [orgData, setOrgData] = useState([]);
    const [loading, setLoading] = useState(false);

    const fetchOrgData = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/organisation/getbyuser/' + currentUser._id)

        setLoading(false);
        console.log(res.status)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setOrgData(data)
        }
    }

    useEffect(() => {
        fetchOrgData();
    }, [])

    const displayData = () => {
        if (!loading && orgData) {
            return (

                <div>
                    {
                        orgData.map((item) => (
                            <div className="card mt-3">
                                <div className="card-body">
                                    <h1 className="card-title">Organisation Name : {item.org_name}</h1>
                                    <p className="card-text">
                                        <b>About Organisation </b>: {item.org_details}
                                    </p>
                                    <p>
                                        <b>Email : </b> {orgData.email}
                                    </p>
                                </div>
                            </div>
                        )



                        )
                    }
                </div>
            )



        }
    }







    return (
        <div className='container'>

            {displayData()}
        </div>
    )
}

export default DisplayDetails
